// Logger, usage: log(this, "");
const log = global.get('logger');
log(this, "Discharge at **max. power**");

// INPUT
let batteries = msg.batteries;
let solution_array = [];

// build solution
msg.batteries.forEach(battery => {
    const calculatedPower = Number(battery.discharging_max);

    solution_array.push({
        id: battery.id,
        mode: "discharge",
        power: calculatedPower
    });

});

// return solution
msg.solutions = solution_array;
return msg;

// Logger, usage: log(this, "");
const log = global.get('logger');

// Which sub-strategy to use
msg.target = "Self-consumption";

// Set target grid consumption for the PID controller
msg.house_target_grid_consumption_in_w = -msg.house_battery_strategy_sell_target_power; // negative, we are exporting to grid.
// tell PID to avoid charge solutions during Selling.
RED.util.setMessageProperty(msg,"advanced_settings.charge_disabled",true,true);

// Finally
log(this, `**Regulated** discharging. Max. grid power: ${Math.floor(msg.house_battery_strategy_sell_target_power)} W`);
return msg;

// Lower bound
let output = msg.sell.threshold_energy | 0; // kWh
output = Math.max(output, 0);

// Upper bound
let max_reserve = 0; // kWh
msg.batteries.forEach(battery => {
    max_reserve += (Number(battery.energy_max*battery.soc_max) - Number(battery.energy_max*battery.soc_min))/100; // kWh
});
output = Math.min(output, max_reserve);

node.status({fill:"blue",shape:"dot",text:`${output}`});

// Output
msg.payload = output;

return msg;

const log = global.get("logger");

// Unkown export goal
log(this, `**${msg.sell.goal}**; Sell goal not recognized. Fallback to **Full Stop**`,"warn");

// Full stop
msg.target = "Full stop";

return msg;

// 1. Get the custom logger from global context
const log = global.get("logger");
log(this,`Sell until: ${msg.sell.goal}`);

// 2. Extract energy level from msg (default to 0 if property is missing)
const energy_remaining = Number(RED.util.getMessageProperty(msg, "batteries_available_energy")) || 0;
var energy_threshold = Number(RED.util.getMessageProperty(msg,"sell.threshold_energy"));
if (energy_threshold === undefined || energy_threshold < 0){
    log(this,`Desired energy reserve not set correctly '${energy_threshold}' kWh`,"warn");
    energy_threshold = 0;
}

// 3. Logic: Determine if energy level is at or below zero
if (energy_remaining <= energy_threshold) {
    msg.sell.threshold_reached = true;

    // User friendly feedback via the custom logger
    log(this, `[STOP] Energy reserve at ${energy_remaining.toFixed(1)} / ${energy_threshold.toFixed(1)} kWh`, 'info');
} else {
    msg.sell.threshold_reached = false;

    // User friendly feedback via the custom logger
    log(this, `[OK] Energy reserve at ${energy_remaining.toFixed(1)} / ${energy_threshold.toFixed(1)} kWh.`, 'info');
}

// 4. Update node status for visual feedback in the editor
node.status({ 
    fill: msg.sell.threshold_reached ? "red" : "green", 
    shape: "dot", 
    text: `Threshold reached: ${msg.sell.threshold_reached}` 
});

return msg;

// 1. Get the custom logger from global context
const log = global.get("logger");
log(this, `Sell until: ${msg.sell.goal}`);

// 2. Extract average SoC and desired SoC level
let batteries = msg.batteries;
let soc_avg = 0;
let count = 0; // could use msg.battery_count
batteries.forEach(battery => {
    soc_avg += battery.soc;
    count++;
});
// 2a.  Current avg. SoC
soc_avg = Math.round(soc_avg / count * 100) / 100;
// 2b.  Sell until SoC (12% as fallback)
var soc_threshold = Number(RED.util.getMessageProperty(msg,"sell.threshold_soc"));
if (soc_threshold === undefined || soc_threshold < 12){
    log(this, `Desired SoC not set correctly '${soc_threshold}' %. I've set it to 12%.`,"warn");
    soc_threshold = 12;
}

// 3. Logic: Determine if SoC level is at or below desired level
if (soc_avg <= msg.payload) {
    msg.sell.threshold_reached = true;

    // User friendly feedback via the custom logger
    log(this, `[STOP] Average SoC at ${soc_avg.toFixed(1)}/${soc_threshold.toFixed(1)} %.`, 'info');
} else {
    msg.sell.threshold_reached = false;

    // User friendly feedback via the custom logger
    log(this, `[OK] Average SoC at ${soc_avg.toFixed(1)}/${soc_threshold.toFixed(1)} %.`, 'info');
}

// 4. Update node status for visual feedback in the editor
node.status({
    fill: msg.sell.threshold_reached ? "red" : "green",
    shape: "dot",
    text: `Threshold reached: ${msg.sell.threshold_reached}`
});

return msg;

// 1. Get the custom logger from global context
const log = global.get("logger");
log(this,`Sell until: ${msg.sell.goal}`);

// 2. Extract energy level from msg (default to 0 if property is missing)
const energy_remaining = Number(RED.util.getMessageProperty(msg, "batteries_available_energy")) || 0;

// 3. Logic: Determine if energy level is at or below zero
if (energy_remaining <= 0) {
    msg.sell.threshold_reached = true;

    // User friendly feedback via the custom logger
    log(this, `[STOP] All batteries are empty or at SoC_min_ (${energy_remaining} kWh).`, 'info');
} else {
    msg.sell.threshold_reached = false;

    // User friendly feedback via the custom logger
    log(this, `[OK] Battery level ${energy_remaining.toFixed(2)} kWh above 0 kWh.`, 'info');
}

// 4. Update node status for visual feedback in the editor
node.status({ 
    fill: msg.sell.threshold_reached ? "red" : "green", 
    shape: "dot", 
    text: `Threshold reached: ${msg.sell.threshold_reached}` 
});

return msg;

const log = global.get("logger");

if(msg.sell.threshold_reached){
    log(this,`Stop discharging, Sell until ${msg.sell.goal} reached.`);
} else {
    log(this,`Continue discharging.`);
}

return msg;

const handle = global.get('unhandledException');

handle(this);

return msg;
